export const getTimeDifference=e=>{let t=new Date,n=new Date(e),r=t-n,o=t.getMonth()-n.getMonth()+12*(t.getFullYear()-n.getFullYear());t.getDate()<n.getDate()&&(o-=1);let a=t.getDate()-n.getDate();if(a<0){let l=new Date(t.getFullYear(),t.getMonth(),0);a+=l.getDate()}let c=Math.floor(r%864e5/36e5),u=Math.floor(r%864e5%36e5/6e4),i="";return o>0&&(i+=`${o} months `),a>0&&(i+=`${a} days `),c>0&&(i+=`${c} hrs `),(i+=`${u} mins`).trim()};export const formatDate=e=>{let t=new Date(e),n={year:"numeric",month:"short",day:"numeric",hour:"numeric",minute:"numeric",second:"numeric",hour12:!0};return t.toLocaleString("en-US",n)};export function currentDate(){var e,t=new Date,n=String(t.getDate()).padStart(2,"0"),r=t.getMonth();return`${n}-${["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"][r]}-${t.getFullYear()}`}export function newFormatDate(e){var t,n=new Date(e),r=n.getDate(),o=n.getMonth();return(r=r<10?"0"+r:r)+"-"+["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"][o]+"-"+n.getFullYear()}export function numberCounter(){document.querySelectorAll(".num").forEach(e=>{let t=0,n=parseInt(e.getAttribute("data-val")),r=setInterval(function(){t+=1,e.textContent=t,t==n&&clearInterval(r)},Math.floor(4e3/n))})}export function mySrcFunction(){var e=document.getElementById("search_off");"OFF"===e.innerHTML?e.innerHTML="<span style='color:darkcyan;'>ON</span>":e.innerHTML="OFF"}export function myLockFunction(){var e=document.getElementById("lock_label");"Unlock"===e.innerHTML?e.innerHTML="<span style='color:orangered;'>Lock</span>":e.innerHTML="Unlock"}export function activeTableRow(e){$(e).addClass("clicked").siblings().removeClass("clicked")}