export const getTimeDifference=e=>{const t=new Date,n=new Date(e),r=t-n;let o=t.getMonth()-n.getMonth()+12*(t.getFullYear()-n.getFullYear());t.getDate()<n.getDate()&&(o-=1);let a=t.getDate()-n.getDate();if(a<0){a+=new Date(t.getFullYear(),t.getMonth(),0).getDate()}const c=Math.floor(r%864e5/36e5);let l="";return o>0&&(l+=`${o} months `),a>0&&(l+=`${a} days `),c>0&&(l+=`${c} hrs `),l+=`${Math.floor(r%864e5%36e5/6e4)} mins`,l.trim()};export const formatDate=e=>new Date(e).toLocaleString("en-US",{year:"numeric",month:"short",day:"numeric",hour:"numeric",minute:"numeric",second:"numeric",hour12:!0});export const modernDateFormat=e=>{const t=new Date(e),n=String(t.getDate()).padStart(2,"0"),r=t.toLocaleString("en-US",{month:"short"}),o=t.getFullYear();let a=t.getHours();const c=a>=12?"PM":"AM";a%=12,a=a||12;return`${n}-${r}-${o}, ${`${a}:${String(t.getMinutes()).padStart(2,"0")}:${String(t.getSeconds()).padStart(2,"0")} ${c}`}`};export function currentDate(){var e=new Date,t=String(e.getDate()).padStart(2,"0"),n=e.getMonth(),r=e.getFullYear();return`${t}-${["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"][n]}-${r}`}export function newFormatDate(e){var t=new Date(e),n=t.getDate(),r=t.getMonth(),o=t.getFullYear();return(n=n<10?"0"+n:n)+"-"+["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"][r]+"-"+o}export function formatNumber(e){return parseInt(e).toLocaleString("en-IN")}export function numberCounter(){document.querySelectorAll(".num").forEach(e=>{let t=0,n=parseInt(e.getAttribute("data-val")),r=Math.floor(4e3/n),o=setInterval(function(){t+=1,e.textContent=t,t==n&&clearInterval(o)},r)})}export function mySrcFunction(){var e=document.getElementById("search_off");"OFF"===e.innerHTML?e.innerHTML="<span style='color:darkcyan;'>ON</span>":e.innerHTML="OFF"}export function myLockFunction(){var e=document.getElementById("lock_label");"Unlock"===e.innerHTML?e.innerHTML="<span style='color:orangered;'>Lock</span>":e.innerHTML="Unlock"}export function activeTableRow(e){$(e).addClass("clicked").siblings().removeClass("clicked")}export function editTableRowSinge(e){$(e).addClass("active-row").siblings().removeClass("active-row")}